import React, { useState } from 'react'
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome"
import {faPen , faTrash} from '@fortawesome/free-solid-svg-icons';
import axios from 'axios';
import CompanyUpdateModal from './CompanyUpdateModal';
import CompanyDeleteModal from './CompanyDeleteModal';


const OneCompany = () => {
    const [openDelete,setOpenDelete] = useState(false);
    const [open, setOpen] = useState(false);
    const handleOpenUpdate = () => setOpen(true);
    const handleCloseUpdate = () => setOpen(false);
    const handleOpenDelete = () => setOpenDelete(true);
    const handleCloseDelete = () => setOpenDelete(false);

    const handleDelete= async (id)=>{
        try {
          console.log(id);
          await axios.delete(`http://localhost:8000/miroir/api/roles/${id}/delete/`)
          props.setReload(!props.relaod)
          props.fetchRoles()
        } catch (error) {
          throw error
        }
      }
    const handleUpdate= async (id,body)=>{
        try {
          console.log(id);
          await axios.put(`http://localhost:8000/miroir/api/roles/${id}/`,body)
          props.setReload(!props.relaod)
          props.fetchRoles()
        } catch (error) {
          throw error
        }
      }
  return (
    <div class="cardRole">
        <CompanyUpdateModal open={open} handleCloseUpdate={handleCloseUpdate} role={props.role} handleUpdate={handleUpdate}  />
        <CompanyDeleteModal handleClose={handleCloseDelete} open={openDelete} role={props.role} handleDelete={handleDelete} />
          <div class="card-details">
            <p class="text-title">{props.role.role}</p>
          </div>
          <div class="card-button">
                <FontAwesomeIcon icon={faPen} className="roleButtons" onClick={(e)=>{
                    e.preventDefault()
                    handleOpenUpdate()
                }} />
                <FontAwesomeIcon icon={faTrash} className="roleButtons" onClick={(e)=>{
                    e.preventDefault()
                    handleOpenDelete()
                }} />
          </div>
        </div>
  )
}

export default OneCompany
