import React, { useEffect, useState } from 'react';
import OneUser from '../OneUser/OneUser';
import "./allUsers.css";
import axios from 'axios';




const AllUsers = (props) => {
  const [details,setDetails] = useState({})

  const handleDetails = async (id) =>{
    try {
      const details = await axios.get(`http://localhost:8000/miroir/api/user_role_company/${id}/`)
      props.setReload(!props.reload)
      props.fetchUsers()
      console.log(details.data[0]);
      setDetails(details.data[0])
    } catch (error) {
      throw error
    }
  }
  useEffect(()=>{
    
  },[props.reload])
  return (
    <div className="allUsersContent">
      <table className="table">
        <thead>
          <tr className='optionsUsers'>
            <th scope="col">Profile image</th>
            <th scope="col">Username</th>
            <th scope="col">CIN</th>
            <th scope="col">Role</th>
            <th scope="col">Company</th>
            <th scope="col">Blocked</th>
            <th scope="col"></th>
            <th scope="col"></th>
        
          </tr>
        </thead>
        <tbody>
          {
           props.users.map((user,index)=>{
                handleDetails(user.id)
                return (<OneUser user={user} key={index}  setReload={props.setReload} reload={props.reload} fetchUsers={props.fetchUsers} details={details} />) 
            })
          }
        </tbody>
      </table>
    </div>
  );
}

export default AllUsers;
