import * as React from "react";
import Box from "@mui/material/Box";
import Button from "@mui/material/Button";
import Modal from "@mui/material/Modal";
import "./basicModal.css";
import axios from "axios";

const style = {
  position: "absolute",
  top: "50%",
  left: "50%",
  transform: "translate(-50%, -50%)",
  width: 400,
  bgcolor: "background.paper",
  border: "2px solid black",
  boxShadow: 24,
  p: 4,
  borderRadius: "10px",
};

export default function BasicModal() {
  const [open, setOpen] = React.useState(false);
  const [image, setImage] = React.useState("");
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);

  const profileUpload= async (e)=>{
    const formData=new FormData()
    formData.append("file",e.target.files[0])
    formData.append("upload_preset","oztadvnr")
    await axios.post("https://api.cloudinary.com/v1_1/dl4qexes8/upload",formData).then((response)=>{
      console.log(response.data["secure_url"]);
      setImage(response.data["secure_url"])

    
    }).catch((error)=>{
      throw error
    })
    
    }

    React.useEffect(()=>{

    },[image])



  return (
    <div>
      <Button className="addBtn" onClick={handleOpen}>
        Add
      </Button>
      <Modal
        open={open}
        aria-labelledby="modal-modal-title"
        aria-describedby="modal-modal-description"
      >
        <Box sx={style}>
          <div className="form">
            <input type="text" placeholder="First name" className="textInputs" />
            <input type="text" placeholder="Last name" className="textInputs" />
            <input type="text" placeholder="CIN" className="textInputs" />
            <input type="text" placeholder="Role" className="textInputs" />
            <input type="date" placeholder="Date" />
            {/* Replacing the input with the custom file upload label */}
            <label className="custum-file-upload" htmlFor="file">
              <div className="icon">
                <svg xmlns="http://www.w3.org/2000/svg" fill="" viewBox="0 0 24 24">
                  {/* SVG code */}
                </svg>
              </div>
              <div className="text">
                <span>{image.trim(10)+"..."}</span>
              </div>
              <input type="file" id="file"  onChange={(e)=>profileUpload(e)}  defaultValue={image} />
            </label>
            {/* End of custom file upload label */}
            <div className="modalButtons">
              <Button className="modalBtn" onClick={handleClose}>Add</Button>
              <Button className="modalBtn" onClick={handleClose}>Cancel</Button>
            </div>
          </div>
        </Box>
      </Modal>
    </div>
  );
}
