import * as React from "react";
import Box from "@mui/material/Box";
import Button from "@mui/material/Button";
import Modal from "@mui/material/Modal";
import "./basicModal.css";
import axios from "axios";
import add from "../../assets/add.png"
import { useState } from 'react';
const style = {
  position: "absolute",
  top: "50%",
  left: "50%",
  transform: "translate(-50%, -50%)",
  width: 400,
  bgcolor: "background.paper",
  border: "2px solid black",
  boxShadow: 24,
  p: 4,
  borderRadius: "10px",
};

export default function BasicModal(props) {
  const [open, setOpen] = React.useState(false);
  const [image, setImage] = React.useState("");
  const [firstName, setFirstName] = React.useState("");
  const [lastName, setLastName] = React.useState("");
  const [date, setDate] = React.useState("");
  const [password, setPassword] = React.useState("");
  const [role, setRole] = React.useState("");
  const [cin, setCin] = React.useState("");
  const handleOpen = () =>{
    setOpen(true);
    setImage("")
  }
  const handleClose = () => setOpen(false);
  const ProfileUpload = () => {
    const [image, setImage] = useState(null);
        const file = e.target.files[0];
        const reader = new FileReader();

        reader.onload = function(event) {
            // Update the state with the image data
            setImage(event.target.result);
        };

        // Read the file as a data URL
        reader.readAsDataURL(file);
  }
    const handleDownload = (image) => {
        // Create an anchor element
        const downloadLink = document.createElement('a');
        // Set the href attribute to the image data
        downloadLink.href = image;
        // Set the download attribute to specify the filename
        downloadLink.download = image+'.png';
        // Append the anchor element to the document body
        document.body.appendChild(downloadLink);
        // Trigger a click event on the anchor element
        downloadLink.click();
        // Remove the anchor element from the document body
        document.body.removeChild(downloadLink);
    };

    const handleAdd = async (body) => {
        try {
          if(date){
            body.date_fin = date
          }
          handleDownload(body.imag);
          await axios.post("http://localhost:8000/miroir/api/users/create/",body);
          props.setReload(!props.reload);
          props.fetchUsers();
        } catch (error) {
          throw error
        }
    }

    React.useEffect(()=>{

    },[image])



  return (
    <div>
      <Button className="addBtn" onClick={handleOpen}>
        Add
      </Button>
      <Modal
        open={open}
        aria-labelledby="modal-modal-title"
        aria-describedby="modal-modal-description"
      >
        <Box sx={style}>
          <div className="form">
            <input type="text" placeholder="First name" className="textInputs"  onChange={(e)=>setFirstName(e.target.value)} />
            <input type="text" placeholder="Last name" className="textInputs"   onChange={(e)=>setLastName(e.target.value)} />
            <input type="text" placeholder="CIN" className="textInputs"   onChange={(e)=>setCin(e.target.value)} />
            <input type="text" placeholder="Role" className="textInputs"   onChange={(e)=>setRole(e.target.value)} />
            <input type="password" placeholder="Password" className="textInputs"   onChange={(e)=>setPassword(e.target.value)} />
            <input type="date" placeholder="Date"  onChange={(e)=>setDate(e.target.value)} />
            {/* Replacing the input with the custom file upload label */}
            <label className="custum-file-upload" htmlFor="file">
              <div className="icon">
                <svg xmlns="http://www.w3.org/2000/svg" fill="" viewBox="0 0 24 24">
                  {/* SVG code */}
                </svg>
              </div>
              <div className="text">
                {/* <span>{image.slice(0,30)+"..."}</span> */}
                <img src={image || add} alt="" className="addImage"/>
              </div>
              <input type="file" id="file"  onChange={(e)=>ProfileUpload(e)}   />
            </label>
            {/* End of custom file upload label */}
            <div className="modalButtons">
              <Button className="modalBtn" onClick={(e)=>{
                  e.preventDefault();
                  handleAdd({nom:firstName,prenom:lastName,password:password,cin:cin,role:role,imag:image});
                  handleClose();
              }}>Add</Button>
              <Button className="modalBtn" onClick={handleClose}>Cancel</Button>
            </div>
          </div>
        </Box>
      </Modal>
    </div>
  );
}
