import React, { useState } from 'react'
import "./oneUser.css"
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome"
import {  faLock , faLockOpen , faPen , faTrash} from '@fortawesome/free-solid-svg-icons';
import UpdateModal from '../OverView/UpdateModal';
import axios from 'axios';

const OneUser = (props) => {
  const [block,setBlocked] = useState(props.user.is_active)
  const [open, setOpen] = React.useState(false);

  const handleOpenUpdate = () => setOpen(true);
  const handleCloseUpdate = () => setOpen(false);
  const handleBlock =  () => {
      setBlocked(!block)
  }
  const handleUpdate= async (id)=>{
    try {
      console.log(id);
      await axios.put(`http://localhost:8000/miroir/api/users/${id}/edit/`)
    } catch (error) {
      throw error
    }
  }
  return (
   
    <tr className="oneTip">
    <td>
      <img className="img_tip" src={props.user.imag} alt=''/>
    </td>
    <td>
      <h5>{props.user.nom+" "+props.user.prenom}</h5>
    </td>
    <td>
      <h5>{props.user.cin}</h5>
    </td>

    <td>
      <label className="date_tip">
        {props.user.role}
      </label>
    </td>
    <td>
    {( block && true )?<FontAwesomeIcon icon={faLockOpen} className='lock'  onClick={()=>{handleBlock()}}/>  :
            <FontAwesomeIcon icon={faLock} className='lock' style={{color:"red"}} onClick={()=>{handleBlock()}} />
            }
    </td>
    
    <td className='buttons' >
    <FontAwesomeIcon icon={faTrash} className='lock' style={{color:"red"}}  />
    </td>
    <td className='buttons' >
     
    <FontAwesomeIcon icon={faPen} className='lock'   onClick={handleOpenUpdate} />

    </td>

    <UpdateModal open={open} handleCloseUpdate={handleCloseUpdate} user={props.user}  handleUpdate={handleUpdate} />

  </tr>
   
  )
}

export default OneUser
