import React, { useEffect, useState } from 'react'
import { useDispatch } from 'react-redux'
import { getAllUsers } from '../../redux/userSlice'
import AllUsers from './AllUsers'
import BasicModal from '../OverView/BasicModal'
import axios from 'axios'

const UsersView = () => {
    const [users,setUsers] = useState([])
    const [reload,setReload] = useState(false)
    const [details,setDetails] = useState({})

    const dispatch = useDispatch()
    const handleDetails = async (id) =>{
      try {
        const details = await axios.get(`http://localhost:8000/miroir/api/user_role_company/${id}/`)
        setReload(!props.relaod)
        fetchUsers()
        console.log(details.data[0]);
        setDetails(details.data[0])
      } catch (error) {
        throw error
      }
    }
    const fetchUsers = async ()=>{
      const users = await dispatch(getAllUsers())
      setUsers(users.payload.reverse())
      console.log(users.length);
    }
    useEffect(()=>{
      fetchUsers()
    },[reload,users.length])
  return (
    
    <div>
        <BasicModal   setReload={setReload} reload={reload} fetchUsers={fetchUsers} />
        <AllUsers  users={users} setReload={setReload} reload={reload}  fetchUsers={fetchUsers} handleDetails={handleDetails} details={details} />     
    </div>
  )
}

export default UsersView
